from flask_wtf import FlaskForm
from wtforms import StringField, IntegerField, SubmitField
from wtforms.validators import DataRequired, Length

class TeamsSearchForm(FlaskForm):
    yearID = IntegerField('Year ID', default=-1, render_kw={'placeholder': 'Enter the team\'s year ID'})
    leagueID = StringField('League ID', [Length(max=100)], default="", render_kw={'placeholder': 'Enter the team\'s league ID'})
