    CS = IntegerField('Caught Stealing', default=-1, render_kw={'placeholder': 'Enter the team\'s caught stealing'})
    HBP = IntegerField('Batters Hit by Pitch', default=-1, render_kw={'placeholder': 'Enter the team\'s batters hit by pitch'})
    SF = IntegerField('Sacrifice Flies', default=-1, render_kw={'placeholder': 'Enter the team\'s sacrifice flies'})
    RA = IntegerField('Opponent Runs Scored', default=-1, render_kw={'placeholder': 'Enter the team\'s opponent runs'})
    ER = IntegerField('Earned Runs Allowed', default=-1, render_kw={'placeholder': 'Enter the team\'s earned runs'})
    ERA = IntegerField('Earned Runs Average', default=-1, render_kw={'placeholder': 'Enter the team\'s earned runs average'})
    CG = IntegerField('Complete Games', default=-1, render_kw={'placeholder': 'Enter the team\'s complete games'})
    SHO = IntegerField('Shoutouts', default=-1, render_kw={'placeholder': 'Enter the team\'s shoutouts'})
    SV = IntegerField('Saves', default=-1, render_kw={'placeholder': 'Enter the team\'s saves'})
