    DP = IntegerField('Double Plays', default=-1, render_kw={'placeholder': 'Enter the team\'s double plays'})
    FP = IntegerField('Fielding Percentage', default=-1, render_kw={'placeholder': 'Enter the team\'s fielding percentage'})
