    teamIDretro = StringField('TeamID Used by Retrosheet', [Length(max=100)], default="", render_kw={'placeholder': 'Enter the team\'s teamID used by Retrosheet'})

    submit = SubmitField('Search', render_kw={'class': 'btn btn-outline-secondary'})



